import React, { useState, useEffect } from "react"

const BestScore = ({ score }) => {
	const [bestScore, setBestScore] = useState(0)
	useEffect(() => {
		if (score > bestScore) {
			setBestScore(score)
		}
	}, [score, bestScore])

	return (
		<div>
			<h2>Your best score: {bestScore}</h2>
			<h2>Your score: {score}</h2>
		</div>
	)
}

export default BestScore
